INSERT INTO `feedbacks` (`id`, `name`, `content`, `created_at`, `updated_at`, `created_by`, `updated_by`, `recycled`, `recycled_at`, `display`, `position`) VALUES
(1, 'Foundation', '"Just a quick line to pass on our thanks and to say how impressed  with the customer service quality we received this afternoon from Kyle  Arnold and John Crookes. They were extremely helpful and patient in  explaining the different functions and capabilities of our relatively  new machine.\n<p>Their experience proved invaluable this afternoon and we would be grateful if this could be recognised in some way."</p>\n<p>Many thanks,<br />Catherine Wright</p>', '2011-01-13 14:25:13', '2011-01-13 14:25:13', 1, NULL, 0, NULL, 1, 0);
