INSERT INTO `green_arena_texts` (`id`, `content`, `created_at`, `updated_at`, `created_by`, `updated_by`, `recycled`, `recycled_at`, `display`, `position`) VALUES
(1, 'We comply with legal environmental requirements.', '2011-01-11 13:25:08', '2011-02-01 16:12:01', 1, 1, 0, NULL, 1, 0),
(2, 'We aim to reduce pollution.', '2011-01-11 13:29:27', '2011-02-01 16:12:11', 1, 1, 0, NULL, 1, 0),
(3, 'We recycle and minimise packaging.', '2011-01-11 13:29:35', '2011-02-01 16:12:24', 1, 1, 0, NULL, 1, 0),
(4, 'We promote the re-use of products, recycle waste &amp; reduce energy consumption.', '2011-01-11 13:30:05', '2011-02-08 15:31:38', 1, 1, 0, NULL, 1, 0),
(5, 'We consider the environmental impact of the products &amp; services we purchase, including company vehicles.', '2011-01-11 13:30:20', '2011-02-08 15:31:39', 1, 1, 0, NULL, 1, 0),
(6, 'We promote our awareness of environmental issues to customers, employees, suppliers and sub-contractors.', '2011-01-12 15:52:38', '2011-02-01 16:12:50', 1, 1, 0, NULL, 1, 0),
(7, 'We are committed to a program of continuous improvement.', '2011-01-12 15:52:46', '2011-02-01 16:12:57', 1, 1, 0, NULL, 1, 0),
(8, 'We have an established &amp; maintained Environmental Management System (EMS).', '2011-01-12 15:53:03', '2011-02-08 15:31:40', 1, 1, 0, NULL, 1, 0),
(9, 'We monitor our performance against targets and we review our environmental policy on a regular basis.', '2011-01-12 15:53:19', '2011-02-01 16:13:21', 1, 1, 0, NULL, 1, 0);
INSERT INTO `home_highlights` (`id`, `name`, `summary`, `link`, `created_at`, `updated_at`, `created_by`, `updated_by`, `recycled`, `recycled_at`, `display`, `position`, `image_file_name`, `image_content_type`, `image_file_size`, `image_updated_at`, `image_alt`, `external_link`, `external_link_new_window`) VALUES
(3, 'Reed Boardall Cold Storage', 'I''ve been very impressed; we are in control of all our documentation, have improved our processes and greatly improved our service to customers.\r\n<a href="http://www.juneaucc.com/">louis vuitton outlet</a>\r\n<a href="http://www.juneaucc.com/">louis vuitton outlet online</a>\r\n<a href="http://www.juneaucc.com/">cheap louis vuitton</a>\r\n', '/categories/4', '2011-02-01 17:12:16', '2015-09-08 11:29:21', 1, 2, 0, NULL, 1, 0, 'ReedBoardall.jpg', 'image/jpeg', 301169, '2011-02-01 17:12:16', 'Reed Boardall Cold Storage', 'http://www.arenagroup.net/case_studies/reed-boardall--2', 1),
(4, 'Lookers Plc', 'The managed print service has delivered a host of benefits and is on track to deliver a high six figure saving over the five year contract period. ', '/case_studies/37', '2013-11-22 11:10:35', '2014-06-01 18:55:34', 3, 3, 0, NULL, 1, 0, 'Lookers2.jpg', 'image/jpeg', 379815, '2013-11-22 11:10:28', 'Lookers', NULL, 1),
(5, 'Community Integrated Care', 'Arena showed such a degree of commitment from our initial meeting. We knew we were dealing with a provider we could trust to deliver a large and complex undertaking.', '/case_studies/65', '2013-11-22 11:15:01', '2013-11-22 11:15:01', 3, NULL, 0, NULL, 1, 0, 'VJT241013RohanGeorge-004.JPG', 'image/jpeg', 126754, '2013-11-22 11:14:59', 'Rohan George CIC', NULL, 1),
(6, 'Liverpool Mutual Homes', 'Arena are top of the PfH framework. It was beneficial to us that Arena had been independently vetted against varying criteria, including value for money and green credentials.', '/case_studies/61', '2013-11-22 11:17:06', '2013-11-22 11:23:26', 3, 3, 0, NULL, 1, 0, 'Julie_Greenland_W_LMH_082013_ws.jpg', 'image/jpeg', 45141, '2013-11-22 11:23:26', '', NULL, 1);
